function pageprocessorAlertus(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var plugins = cordova.require("cordova/plugin_list").metadata;

  if (typeof plugins['com.venkykowshik.startapp'] === "undefined") {

    navigator.notification.alert("Please update your app to use this feature", function() {
      window.location.href = "index.html#home";
      $.unblockUI();
      //$location.path("/home");
    }, "Update required", "OK");
  } else {
    if (window.device.platform == "iOS") {
      var iosUri = pagedef.iosuri;
      navigator.startApp.check(iosUri, function(message) { /* success */
          navigator.startApp.start(iosUri, function(message) { /* success */
              $location.path("/home");
              $.unblockUI();
            },
            function(error) { /* error */
              console.log(error);
            });
        },
        function(error) { /* error */
          $scope.loadurl(pagedef.itunesurl);
          $location.path("/home");
          $.unblockUI();
        });
    } else if (window.device.platform == "Android") {
      var androidpackage = pagedef.androidpackage
      navigator.startApp.check(androidpackage, function(message) { /* success */
          navigator.startApp.start(androidpackage, function(message) { /* success */
              $location.path("/home");
              $.unblockUI();
            },
            function(error) { /* error */
              navigator.notification.alert("Your device isn't compatible with this version");
              $location.path("/home");
              $.unblockUI();
            });
        },
        function(error) { /* error */
          $scope.loadurl(pagedef.playstoreurl);
          $location.path("/home");
          $.unblockUI();
        });
    } else {
      apprise("This feature is not available on Emulator", {
        "verify": false,
        "textYes": "Ok"
      }, function(r) {});
    }


    $scope.loadurl = function(url) {
      if (window.device) {
        $.blockUI();
      }
      var iabRef = window.open(url, device.platform == "Android" ? "_system" : "_blank", "location=no,hidden=yes");
      var hideBlockUi = function() {
        iabRef.show();
        $.unblockUI();
      };
      var iabClose = function(data) {
        window.location.href = "index.html#home";
        $.unblockUI();
      };
      iabRef.addEventListener("exit", iabClose);
      var loadStop = function(data) {
        hideBlockUi();
      };
      iabRef.addEventListener("loadstop", loadStop);
    }
  }
}

