        travelMode: "",
        unitSystem: google.maps.UnitSystem.METRIC
      };

      switch (travelModeOption) {
        case "walk":
          {
            var usertravelMode = "WALKING";
            request.travelMode = google.maps.TravelMode.WALKING;
            break;
          }
        case "cycle":
          {
            var usertravelMode = "BICYCLING";
