function pageprocessorDirectory(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    $scope.resultfound = true;
    // $rootScope.DirectoryResult = null;

    var dv = '<div class="panel-heading" style="background: #838182; border-color: rgb(0, 0, 0);">' + '<form id="srch" class="form-horizontal">' + '<div class="input-group">' + '<input id="Directorytxt" type="text" ng-model="Directoryval" class="form-control" />' + '<span class="input-group-btn">' + '<button  id="srchbtn" ng-click="search()" class="btn btn-primary" type="button" style="background:#000"><span class="fa fa-search"></span>' + '</button>' + '</span>' + '</div>' + '</form>' + '<div class="helptext" style="padding-top: 6px;color:#fff;">Search by Last Name, First Name or Department</div>' + '</div>';
    $(".app-content").prepend($compile(dv)($scope));
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);


    $('#srch').on('submit', function(event) {
        event.preventDefault();
        $scope.search();
        $scope.$digest();
        $("#srchbtn").focus();
        return false;
    });

    $scope.fetched = false;
    $scope.filterresult = function() {
        var searchTxt = $("#Directorytxt").val();
        $rootScope.Directoryval = searchTxt;
        var results = [];
        var patt = new RegExp(searchTxt, "i");
        var filterParams = pagedef.searchfields.split(",");
        
        $.each($scope.DirectoryList.record, function(index, val) {
            var found = false;
            for (var i = 0; i < filterParams.length; i++) {
                if (val["" + filterParams[i] + ""] != undefined && val["" + filterParams[i] + ""].match(patt)) {
                    found = true;
                    break;
                }
            }
            if (found) {
                results.push(val);
            }
        });

        results.sort(function(a, b) {
            if (a.FirstName < b.FirstName)
                return -1;
            if (a.FirstName > b.FirstName)
                return 1;
            return 0;
        });
        $rootScope.DirectoryResult = results;
        if (results.length > 0) {
            $scope.resultfound = true;
            $.unblockUI();
        } else {
            $scope.resultfound = false;
            $.unblockUI();
        }

    };
    $scope.search = function() {
        $.blockUI();
        if ($scope.fetched == false) {
            // var url = pagedef.url+"?m="+Math.random();
            var url = pagedef.url;
            if (window.device) {} else {
                //url = "/websimulator/xml?url=" + pagedef.url+"?m="+Math.random();
                url = "/websimulator/xml?url=" + pagedef.url;

            }
            var xhr = $.ajax({
                url: url,
                dataType: "xml",
                async: true,
                data: {},
                success: function(resp) {
                    try {
                        var results = $.xml2json(resp);

                        $scope.fetched = true;
                        $scope.DirectoryList = results;
                        $scope.filterresult();
                        $scope.$apply();
                        $.unblockUI();
                    } catch (E) {
                        //alert("Exception : " + E);
                    }
                },
                error: function(e) {
                    // alert("Error : " + e);
                    $.unblockUI();
                    apprise("Error occured. Please try again later.", {
                        "verify": false,
                        "textYes": "Ok"
                    }, function(r) {
                        $window.history.back();
                    });
                }
            });
        } else {
            $scope.filterresult();
        }
    }
    $scope.search();
}

function pageprocessorDirectoryDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    $scope.DirectoryDetails = $rootScope.DirectoryResult[$routeParams.index];
    var template = pagedef.datatemplate;
    var data1 = $compile($(template))($scope);
    $("#appContent")
        .append(data1);

    $scope.addContacts = function() {
        var dirContact = navigator.contacts.create();
        var details = $scope.DirectoryDetails;
        if (window.device.platform == "Android") {
            dirContact.displayName = details.DisplayName;
            var phoneNo = details.Telephone;
            if (phoneNo) {
                var phoneNumbers = [1];
                phoneNumbers[0] = new ContactField('work', phoneNo, true); // preferred number  
                dirContact.phoneNumbers = phoneNumbers;
            }

            var personEmail = details.Email;

            if (personEmail) {
                var emails = [1];
                emails[0] = new ContactField('emails', personEmail, false);
                dirContact.emails = emails;
            }

            ///for ios displayName is not supported insted of displayName we can use name object
            var name = new ContactName();

            if (details.FirstNAME == "n/a") {
                name.givenName = "";
            } else {
                name.givenName = details.FirstName;
            }

            if (details.LastNAME == "n/a") {
                name.familyName = "";
            } else {
                name.familyName = details.LastName;
            }

            dirContact.name = name;

            dirContact.save(preLoginSaveContacts, preLoginSaveError);
        }


        if (window.device.platform == "iOS") {

            var phoneNumbers = [2];
            // phoneNumbers[0] = new ContactField('mobile', $("#phone").text(), true); // preferred number
            phoneNumbers[0] = new ContactField('work', details.officePHONE, true); // Work/Dept. number
            dirContact.phoneNumbers = phoneNumbers;

            var emails = [1];
            emails[0] = new ContactField('emails', details.emailADDR, false);
            dirContact.emails = emails;

            ///for ios displayName is not supported insted of displayName we can use name object
            var name = new ContactName();
            if (details.FirstNAME == "n/a") {
                name.givenName = "";
            } else {
                name.givenName = details.FirstNAME;
            }

            if (details.LastNAME == "n/a") {
                name.familyName = "";
            } else {
                name.familyName = details.LastNAME;
            }

            dirContact.name = name;

            dirContact.save(preLoginSaveContacts, preLoginSaveError);
        }

    }

    function preLoginSaveContacts(contact) {
        navigator.notification.alert("Contact Saved Successfully");
    }

    function preLoginSaveError(contactError) {
        if(contactError.code==20){navigator.notification.alert("Permission denied");}
else{
navigator.notification.alert("Contact already saved");
}
    }

}

