function pageprocessorFinAid2(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    var data1 = $compile($(pagedef.datatemplate))($scope);
    $("#appContent").append(data1);
    var title = "Financial Aid";
    
    $rootScope.getAPI($rootScope.middlewareServerUrl + pagedef.endpoint, function(data) {
        $scope.finaidyears = data.finaidyears;
    });
    //
    $scope.stureq = function() {
        
        if ($scope.selyear == "" || $scope.selyear == undefined) {
            var message = "Please select Aid year";
            if (window.device) {
                navigator.notification.alert(message, null, title, null);
            } else {
                apprise(message, {
                    'verify': false,
                    'textYes': "Ok"
                }, function(r) {});
            }
            return;
        }
        $rootScope.selyear = $scope.selyear;
        $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/bannerfinaidstudentrequirements/aidy=" + $scope.selyear, function(data) {
            $rootScope.finaidreqs = data;
            var noResMes = "No requirements exist for the student !";
            if ($scope.finaidreqs.studentrequirements.length == 0) {
                if (window.device) {
                    navigator.notification.alert(noResMes, null, title, null);
                } else {
                    apprise(noResMes, {
                        'verify': false,
                        'textYes': "Ok"
                    }, function(r) {});
                }
                return;
            } else {
                $location.path("/app/FinAid2/FinAid2Page2");
            }            
        });
    }
    $scope.awards = function() {
        if ($scope.selyear == "" || $scope.selyear == undefined) {
            var message = "Please select Aid year";
            if (window.device) {
                navigator.notification.alert(message, null, title, null);
            } else {
                apprise(message, {
                    'verify': false,
                    'textYes': "Ok"
                }, function(r) {});
            }
            return;
        }
        $rootScope.selyear = $scope.selyear;
        /*
         $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/bannerfinaidawards/aidy=" + $scope.selyear, function(data) {
             $rootScope.finaidawards = data;
             $location.path("/app/FinAid2/FinAid2Page3");
         });
         */
        $rootScope.getAPI($rootScope.middlewareServerUrl + "/services/student/bannerfinaidawardoverview/aidy=" + $scope.selyear, function(data) {
            $rootScope.awardoverview = data;
            var coatotal = 0;
            // Calculate COA totals
            $.each(data.costattendance, function(cindex, coarec) {
                coatotal = coatotal + Number(coarec.AMOUNT);
            });
            // Award details 
            var awards = data.awarddetails;
            var sortawards = awards;

            function SortByFundCode(x, y) {
                return ((x.RPRAWRD_FUND_CODE == y.RPRAWRD_FUND_CODE) ? 0 : ((x.RPRAWRD_FUND_CODE > y.RPRAWRD_FUND_CODE) ? 1 : -1));
            }


            sortawards.sort(SortByFundCode);
            var prevfund = null;
            var curfund = null;
            var funddesc = null;
            var prevfunddesc = null;
            var curstatus = null;
            var prevstatus = null;
            var totamt = 0;
            var fundsumm = [];
            var htmlscript = "";
            var found = false;
            var offeramt = 0;
            var prevofferamt = 0;
            $.each(sortawards, function(index, award) {
                found = true;
                curfund = award.RPRAWRD_FUND_CODE;
                funddesc = award.RFRBASE_FUND_TITLE;
                curstatus = award.RTVAWST_DESC;
                offeramt = Number(award.RPRATRM_OFFER_AMT);
                if (index == 0) {
                    prevfund = curfund;
                    prevfunddesc = funddesc;
                    prevstatus = curstatus;
                    prevofferamt = offeramt;
                }
                //alert(prevfund + ' - ' + curfund);
                if (prevfund == curfund) {
                    totamt = totamt + offeramt;
                    //alert('total=' + totamt);
                } else {
                    //alert('total:::' + totamt);
                    fundsumm.push({
                        "FUND": prevfund,
                        "FUND_TITLE": prevfunddesc,
                        "STATUS": prevstatus,
                        "OFFERED": totamt
                    });
                    prevfund = curfund;
                    prevfunddesc = funddesc;
                    prevofferamt = offeramt;
                    totamt = offeramt;
                    //alert('offer total:::' + totamt);
                }

            });
            if (found) {
                fundsumm.push({
                    "FUND": curfund,
                    "FUND_TITLE": funddesc,
                    "STATUS": curstatus,
                    "OFFERED": totamt
                });
            }

            var awardgrandtot = 0;
            $.each(fundsumm, function(index, val) {
                awardgrandtot = awardgrandtot + Number(val.OFFERED);
            });
            $rootScope.awardoverview = {
                "needcalculation": data.CLASSLOOKUP[0],
                "housing": data.housing,
                "awarddetails": data.awarddetails,
                "costattendance": data.costattendance,
                "coatotal": coatotal,
                "cumulativeloaninfo": data.cumulativeloaninfo,
                "awardsummary": fundsumm,
                "awardsummtotal": awardgrandtot
            };

            $location.path("/app/FinAid2/FinAid2Page3");
        });


    }

    $scope.accsumm = function() {
            $rootScope.selyear = $scope.selyear;
