function pageprocessorAthleticsFeed(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $rootScope.schfeeds = null;
  $rootScope.scofeeds = null;
  $rootScope.newsfeeds = null;
  var dv = '<div class="btn-group justified nav-tabs">' +
    '<a style="border-radius:0px;" class="btn btn-default btn-xs" href="#Tab1" toggle="on" active-class="active"><i class="fa fa-bars"></i><br/>News</a>' +
    '<a style="border-radius:0px;" class="btn btn-default btn-xs" href="#Tab2" toggle="on" active-class="active"><i class="fa fa-calendar"></i><br/>Schedule</a>' +
    '<a style="border-radius:0px;" class="btn btn-default btn-xs" href="#Tab3" toggle="on" active-class="active"><i class="fa fa-signal"></i><br/>Scores</a>' +
    '</div>';
  $(".app-content").prepend($compile(dv)($scope));
  $.blockUI();
  var f = $rootScope.feedlist[$routeParams.index];
  $scope.getimage = function(content) {

    return $sce.trustAsHtml(content.split('"')[1]);
  }
  $scope.trustUrl = function(url) {

    return $sce.trustAsUrl(url);
  }
  $scope.trusthtml = function(data) {
    return $sce.trustAsHtml(data);
  };
  $scope.loadnews = function() {
    var newsUrl = f.newsurl;
    $.blockUI();
    var rssreaderUrl = "https://kryptosda.kryptosmobile.com/kryptosds/rss/rssreader";
    var param = {
      "url": newsUrl
    };
    $http.post(rssreaderUrl, param, '').success(function(data, status, headers, config) {
      $scope.newsfeeds = data.feed.entries;
      $rootScope.newsfeeds = data.feed.entries;
      $scope.$apply();
      $.unblockUI();
    }).error(function(error, status, header, config) {
      //alert(JSON.stringify(error));
      $.unblockUI();
    });


  };

  $scope.loadsch = function() {

    $.blockUI();
    var url = f.schurl;

    if (window.device) {} else {
      url = "/websimulator/xml?url=" + f.schurl;
    }


    var xhr = $.ajax({
      url: url,
      dataType: "xml",
      async: true,
      data: {},
      success: function(resp) {
        var results = $.xml2json(resp);
        $scope.schfeeds = results;
        $rootScope.schfeeds = results;
        $scope.$apply();
        $.unblockUI();
      },
      error: function(e) {
        // navigator.notification.alert("Schedule could not be loaded at this time. Please try again later");
        $.unblockUI();

      }
    });

  };


  $scope.loadsco = function() {

    $.blockUI();
    var url = f.scourl;
    var sportsAbbr = f.score;
    var scoreArray = [];

    if (window.device) {} else {
      url = "/websimulator/xml?url=" + f.scourl;
    }

    var xhr = $.ajax({
      url: url,
      dataType: "json",
      async: true,
      data: {},
      success: function(resp) {
        var scoreArray = [];
        for (var i = 0; i < resp.scores.length; i++) {
          if (resp.scores[i].sport_abbrev == sportsAbbr) {
            scoreArray.push(resp.scores[i]);
          }
        }
        $scope.scofeeds = scoreArray;
        $rootScope.scofeeds = scoreArray;
        $scope.$apply();
        //$scope.$digest();
        $.unblockUI();

      },
      error: function(e) {
        // navigator.notification.alert("Schedule could not be loaded at this time. Please try again later");
        $.unblockUI();

      }
    });

  };
  var data = "<div></div>";
  data = $(data)
    .append($(pagedef.datatemplate));
  var data1 = $compile($(data))($scope);
  $("#appContent")
    .append(data1);
  $scope.loadnews();
  $scope.loadsch();
  $scope.loadsco();
  $scope.convertToDate = function(stringDate) {
    var dateOut = new Date(stringDate);
    dateOut.setDate(dateOut.getDate());
    return dateOut;
  };

}

function pageprocessorAthleticsDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
